func main() {
	log.SetFlags(log.Lshortfile | log.Lmicroseconds)
	var err error
	if unconvertBin, err = exec.LookPath("unconvert"); err != nil {
		log.Fatal("Please install the unconvert tool (go get -u github.com/mdempsky/unconvert)")
	}

	flag.Parse()

	sqlite()
	mpTest()
	threadTest1()
	threadTest2()
	threadTest3()
	threadTest4()
}
